 p_id=>wwv_flow_imp.id(957822046936115040)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'GET_EMPLOYEE'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'   v_scale           VARCHAR2 (40)   := TO_NUMBER (apex_application.g_x01);',
'   v_id               VARCHAR2 (40)   := TO_NUMBER (apex_application.g_x02);',
'',
'   v_ename            VARCHAR2 (4000) := ''{"KEY0": {"NAME":"- Select Employee -","VALUE":"","SELECTED":""},'';',
'   v_selected_empno   VARCHAR2 (40);',
'   v_count            NUMBER          := 1;',
'BEGIN',
'',
'   IF v_id IS NOT NULL',
'   THEN',
'      select emp_code',
'      INTO v_selected_empno',
'        from   prj_employee_salary',
'        where emp_code = v_id',
'        order by 1;',
'   END IF;',
'   ',
'   FOR c IN (select emp_name d, emp_code r',
'        from   prj_employee_salary',
'        where ACTIVE = ''Y''',
'        and SALARY_GRADE_ID = v_scale',
'        order by 1)',
'   LOOP',
'      v_ename :=',
'            v_ename',
'         || ''"KEY''',
'         || v_count',
'         || ''":{"NAME":"''',
'         || c.d',
'         || ''","VALUE":"''',
'         || c.r',
'         || ''","SELECTED":"''',
'         || CASE',
'               WHEN c.r = v_selected_empno',
'                  THEN c.d',
'               ELSE NULL',
'            END',
'         || ''"},'';',
'      v_count := v_count + 1;',
'   END LOOP;',
'',
'   v_ename := RTRIM (v_ename, '','');',
'   v_ename := v_ename || ''}'';',
'   HTP.p (v_ename);',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_security_scheme=>'MUST_NOT_BE_PUBLIC_USER'
);
end;
